@Named("eventos")
@SessionScoped
public class ListarEventosBean implements Serializable {

    private static final long serialVersionUID = 1L; 

    @Inject
    private TabelaEventos tabeventos;

    private List<Evento> listaEvento;
    private String parametro;
    private String opcao;


    public void lerTabelaEventos(String tipoacao)
    {
        listaEvento = tabeventos.tabelaEvento(tipoacao);
    }

    public List<Evento> getListaEvento() {

        //System.out.println("Parametro para ler Registros ANTES:"+getOpcao());

        if (getOpcao() == null){
            setOpcao("Todos");
        }
        else {
            setOpcao(parametro);
        }
        //System.out.println("Parametro para ler Registros DEPOIS:"+getOpcao());
        lerTabelaEventos(getOpcao());
        return listaEvento;
    }   

    //Evento Actionlistener
    public void atribuirParametro(ActionEvent event){

        parametro = (String)event.getComponent().getAttributes().get("acao");

        setOpcao(parametro);
        //System.out.println("Parametro de Entrada:"+getOpcao());   
        lerTabelaEventos(getOpcao());

    }

    public String getOpcao() {
        return opcao;
    }


    public void setOpcao(String opcao) {
        this.opcao = opcao;
    }

}
