public class EntityManagerConfig implements Serializable {

    private static final long serialVersionUID = 1L;

    @PersistenceUnit(unitName = "GestorPU")
    private EntityManagerFactory factoryGestor;

    @PersistenceUnit(unitName = "MitryusPU")
    private EntityManagerFactory factoryMitryus;

    @Produces

    @RequestScoped
    @Gestor
    public EntityManager createGestorEntityManager() {
        return factoryGestor.createEntityManager();
    }

    @Produces

    @RequestScoped
    @Default
    public EntityManager createMitryusEntityManager() {
        return factoryMitryus.createEntityManager();
    }

    public void closeEntityManager(@Disposes EntityManager manager) {
        if (manager.isOpen()) {
            manager.close();
        }
    }

    @Retention(RetentionPolicy.RUNTIME)
    @Target({ElementType.METHOD, ElementType.FIELD, ElementType.PARAMETER})
    @Qualifier
    public @interface Gestor {
    }
